// 图标名称类型
export type FontAwesomeIconName = keyof typeof FONTAWESOME_ICONS;

// 获取 FontAwesome 图标
export function getFontAwesomeIcon(name: FontAwesomeIconName): string {
  return FONTAWESOME_ICONS[name] || FONTAWESOME_ICONS["info"];
}

// 检查图标是否存在
export function hasFontAwesomeIcon(name: string): boolean {
  return name in FONTAWESOME_ICONS;
}

// 获取所有可用的图标名称
export function getAvailableFontAwesomeIcons(): FontAwesomeIconName[] {
  return Object.keys(FONTAWESOME_ICONS) as FontAwesomeIconName[];
}
